import styled from 'styled-components'
import fetch from 'isomorphic-unfetch'

import Layout from '../../components/Layout'
import Details from '../../components/Details'


const Home = ({ data }) => (

  <Layout company_name={"Mammoth"} site_name={"University/CEH"}>
    <Details 
      name="Water Tank" 
      currentValue={data[0].D0}
      totalValue={16}
      alarmOn={data[0].D3}
      alarmOff={data[0].D4}
    />

    <Details 
      name="Water Tank" 
      currentValue={data[0].D5}
      totalValue={16}
      alarmOn={data[0].D8}
      alarmOff={data[0].D9}
    />
  </Layout>

)

export async function getServerSideProps(){
  const res = await fetch('http://jct-systems.com/api/university')
  const data = await res.json();

  return {props: { data } }
} 


export default Home
