import styled from 'styled-components'

const MainContainer = styled.main`
  margin: 0;
  padding: 0;
  width: 50%;
  height: 100vh;
  display: flex;
  flex-flow: column;
`

const CompanyContainer = styled.div`
  height: 10%;
  display: flex;
  flex-flow: column;
  justify-content: center;
`

const CompanyName = styled.h1`
  font-size: 6rem;
  font-weight: 600;
  color: #fff;
`

const SiteName = styled.h2`
  font-size: 4rem;
  font-weight: 600;
  color: #FF7E26
`

const SiteDetailsContainer = styled.div`
  background-color: #efefef;
  width: 100%;
`


const Layout = props => (
  <>
    <MainContainer>
      <CompanyContainer>
        <CompanyName>{props.company_name}</CompanyName>
        <SiteName>{props.site_name}</SiteName>
      </CompanyContainer>
      <SiteDetailsContainer>
        { props.children }
      </SiteDetailsContainer>
    </MainContainer>

    <style jsx global>{`
      *{
        margin: 0;
        padding: 0;
        box-sizing: border-box;
        font-size: 10px;
        font-family: sans-serif;
        font-weight: 100;
      }

      body{
        background-color: #292929;
      }

      #__next{
        height: 100%;
        width: 100%;
        display: flex;
        flex-flow: column;
        align-items: center;
        justify-content: center;
      }
    `}</style>
  </>
)

export default Layout
