import { Component } from 'react'
import io from 'socket.io-client'
import fetch from 'isomorphic-unfetch'

import Layout from '../../components/Layout'
import MobileLayout from '../../components/MobileLayout' 
import Details from '../../components/Details'


export default class Site extends Component {

  static defaultProps = {
    company: []
  }

  state = {
    companies: this.props.company
  }

  static async getInitialProps(ctx){
    const { name, site } = ctx.query
    const res = await fetch(`http://jct-systems.com/api/${name}/${site}`);
    const json = await res.json();

    let isMobileView = ctx.req.headers['user-agent'].search(/Android|iPhone/i)

    if(isMobileView < 0){
      return {
        isMobileView: false,
        equipment: json[1]
      }
    } else {
      return {
        isMobileView: true,
        equipment: json[1]
      }
    }
  }


  componentDidMount(){
    this.socket = io('http://localhost:3001/')
    // this.socket.on('company', this.handleMessage)
    this.socket.on('company', data => {
      this.handleMessage(data)
    })
  }

  componentWillUnmount(){
    this.socket.close()
  }

  handleMessage = company => {
    this.setState({
      companies: company
    })
  }

  render(){
    return(
        this.props.isMobileView ?
        <MobileLayout company_name="Doghouse Oil & Gas" site_name="Anchor Battery">
            {
              this.props.equipment.map(equip => (
                <Details 
                key={equip.id} 
                name={equip.name}
                currentValue={equip.currentLevel}
                totalValue={equip.totalLevel}
                alarmOn={equip.on_level}
                alarmOff={equip.off_level}
                mon={equip.monValue}
                tue={equip.tueValue}
                wed={equip.wedValue}
                thu={equip.thuValue}
                fri={equip.friValue}
                sat={equip.satValue}
                sun={equip.sunValue}
                />
                ))
            }
          </MobileLayout>
        :

            this.state.companies.map(comp => {
              <h1 style={{color: 'white'}}>{comp.name}</h1>
            })

          // <Layout company_name="Doghouse Oil & Gas" site_name="Anchor Battery">
          //   {
          //     this.props.equipment.map(equip => (
          //       <Details 
          //       key={equip.id} 
          //       name={equip.name}
          //       currentValue={equip.currentLevel}
          //       totalValue={equip.totalLevel}
          //       alarmOn={equip.on_level}
          //       alarmOff={equip.off_level}
          //       mon={equip.monValue}
          //       tue={equip.tueValue}
          //       wed={equip.wedValue}
          //       thu={equip.thuValue}
          //       fri={equip.friValue}
          //       sat={equip.satValue}
          //       sun={equip.sunValue}
          //       />
          //       ))
          //   }
          // </Layout>


    )
  }
}
