import { Component } from 'react'
import io from 'socket.io-client'
import fetch from 'isomorphic-unfetch'

import Layout from '../../components/Layout'
import MobileLayout from '../../components/MobileLayout' 
import Details from '../../components/Details'


export default class Site extends Component {

  static defaultProps = {
    company: []
  }

  state = {
    companies: []
  }

  static async getInitialProps(ctx){
    const { name, site } = ctx.query
    const res = await fetch(`http://jctsystems.com/portal/status.asp`)
    // const json = await res.json();
    console.log(res);

    let isMobileView = ctx.req.headers['user-agent'].search(/Android|iPhone/i)

    if(isMobileView < 0){
      return {
        isMobileView: false,
        equipment: json[1]
      }
    } else {
      return {
        isMobileView: true,
        equipment: json[1]
      }
    }
  }

  render(){
    return(
        this.props.isMobileView ?
        <MobileLayout company_name="Doghouse Oil & Gas" site_name="Anchor Battery">
            {
              this.props.equipment.map(equip => (
                <Details 
                  key={equip.id} 
                  name={equip.name}
                  currentValue={equip.currentLevel}
                  totalValue={equip.totalLevel}
                  alarmOn={equip.on_level}
                  alarmOff={equip.off_level}
                  mon={equip.monValue}
                  tue={equip.tueValue}
                  wed={equip.wedValue}
                  thu={equip.thuValue}
                  fri={equip.friValue}
                  sat={equip.satValue}
                  sun={equip.sunValue}
                />
              ))
            }
          </MobileLayout>
        :       
          <Layout company_name="Doghouse Oil & Gas" site_name="Anchor Battery">
            {
              this.props.equipment.map(equip => (
                <Details 
                  key={equip.id} 
                  name={equip.name}
                  currentValue={equip.currentLevel}
                  totalValue={equip.totalLevel}
                  alarmOn={equip.on_level}
                  alarmOff={equip.off_level}
                  mon={equip.monValue}
                  tue={equip.tueValue}
                  wed={equip.wedValue}
                  thu={equip.thuValue}
                  fri={equip.friValue}
                  sat={equip.satValue}
                  sun={equip.sunValue}
                />
              ))
            }
          </Layout>          
    )
  }
}
