import { defaults, Line } from 'react-chartjs-2'

import styled from 'styled-components'
import { color } from '../public/screensizes'


const Trend = props => {
  defaults.global.defaultFontFamily = 'Industry'
  defaults.global.defaultFontColor = color.jctLightGray

  const data = {
    labels: ['mon', 'tue', 'wed', 'thu', 'fri', 'sat', 'sun'],
    datasets: [{
      data: [4, 8, 6, 9, 11, 7, 9],
      borderWidth: 3,
      steppedLine: true
    }]
  }

  return(
    <Graph>
      <Line
        data={ data }
        options={{
          legend: false,
          tooltips: false,
          scales: {
            yAxes: [{
              ticks: {
                min: 0,
                max: 16,
                stepSize: 4,
                // padding: 10,
                display: false
              },
              gridLines: {
                drawTicks: false,
                zeroLineWidth: 0
              },
            }],
            xAxes: [{
              ticks: {
                padding: 10
              },
              gridLines: {
                drawTicks: false,
                zeroLineWidth: 0,
                display: false
              }
            }]
          }
        }}
      />
    </Graph>
  )
}

export default Trend

const Graph = styled.div`
  width: 100%;
`
