import styled from 'styled-components'
import { standard, retina, color } from '../public/screensizes'

const MainContainer = styled.main`
  height: 100vh;
  display: grid;

  @media ${standard.tabletP}{
    width: 80%;
    grid-template-rows: 10% 90%;
  }

  @media ${standard.tabletL}{
    width: 90%;
    grid-template-rows: 15% 85%;
  }

  @media ${standard.desktop} {
    max-width: 50%;
    grid-template-rows: 10% 90%;
  }

  @media ${retina.laptopM} {
    width: 65%;
    grid-template-rows: 10% 90%;
  }

`

const CompanyContainer = styled.div`
  margin: 2rem 0 2rem 1rem;
  display: flex;
  flex-flow: column;
  justify-content: center;


  @media ${standard.tablet}{
    margin-left: 1rem;
  }

  @media ${standard.desktop}{
    margin: 0 0 0 1rem;
  }

  @media ${retina.laptopM}{
    margin: 3rem 0 3rem 1rem;
  }
`

const CompanyName = styled.h1`
  font-weight: 600;
  color: #efefef;

  @media ${standard.tablet} {
    font-size: 3rem;
  }

  @media ${standard.desktop} {
    font-size: 5rem;
  }

  @media ${retina.laptopM} {
    font-size: 4rem;
  }
`


const SiteName = styled.h2`
  font-weight: 600;
  color: #FF7E26;

  @media ${standard.tablet} {
    font-size: 2rem;
  }

  @media ${standard.desktop} {
    font-size: 3rem;
  }

  @media ${retina.laptopM} {
    font-size: 2.75rem;
  }
`

const SiteDetailsContainer = styled.div`
  background-color: ${color.jctWhite};
  height: auto;
  padding: 9rem 18rem 9rem 0;
  overflow: scroll;
  display: grid;

  @media ${standard.tabletP}{
    padding: 3rem 6rem 3rem 3rem;
    grid: auto-flow / repeat(2, 50%);
    grid-gap: 3rem 3rem;
  }

  @media ${standard.tabletL}{
    padding: 4rem 13rem 4rem 4rem;
    grid: auto-flow / repeat(3, 33%);
    grid-gap: 5rem 5rem;
  }

  @media ${standard.desktop} {
    padding: 6rem 27rem 6rem 6rem;
    grid: auto-flow / repeat(4, 25%);
    grid-gap: 10rem 7rem;

  }

  @media ${retina.laptopM} {
    padding: 5rem 19rem 0 5rem;
    grid: auto-flow / repeat(3, 33%);
    grid-gap: 1rem 7rem;
  }
`

const Layout = props => (
  <>

    <MainContainer>
      <CompanyContainer>
        <CompanyName>{props.company_name}</CompanyName>
        <SiteName>{props.site_name}</SiteName>
      </CompanyContainer>
      <SiteDetailsContainer>
        { props.children }
      </SiteDetailsContainer>
    </MainContainer>

    <style jsx global>{`
      *{
        margin: 0;
        padding: 0;
        box-sizing: border-box;
        font-size: 10px;
      }

      body{
        background-color: #292929;
      }

      #__next{
        height: 100%;
        width: 100%;
        display: flex;
        flex-flow: column;
        align-items: center;
        justify-content: center;
      }
    `}</style>

  </>
)

export default Layout
