  <Container>
    <Title>Alarms</Title>

    <AlarmList>
      <AlarmListItem>On: {props.on}</AlarmListItem>
      <AlarmListItem>Off: {props.off}</AlarmListItem>
    </AlarmList>
  </Container>
)

export default Alarms
