import styled from 'styled-components'
import { standard, retina, color } from '../public/screensizes'

const MainContainer = styled.main`
  margin: 0;
  padding: 0;
  height: 100vh;
  display: flex;
  flex-flow: column;


  @media ${standard.tablet}{
    width: 90%;
  }

  @media ${standard.desktop} {
    width: 50%;
  }


  @media ${retina.laptop} {
    width: 75%;
  }

`

const CompanyContainer = styled.div`
  margin: 2rem 0 2rem 1rem;
  display: flex;
  flex-flow: column;
  justify-content: center;

  @media ${standard.tablet}{
    margin: 5rem 0 5rem 1rem;
  }
`

const CompanyName = styled.h1`
  font-weight: 600;
  color: #efefef;

  @media ${standard.tablet} {
    font-size: 3rem;
  }

  @media ${standard.desktop} {
    font-size: 6rem;
  }

  @media ${retina.laptop} {
    font-size: 4rem;
  }
`


const SiteName = styled.h2`
  font-weight: 600;
  color: #FF7E26;

  @media ${standard.tablet} {
    font-size: 2rem;
  }

  @media ${standard.desktop} {
    font-size: 4rem;
  }

  @media ${retina.laptop} {
    font-size: 2.75rem;
  }
`

const SiteDetailsContainer = styled.div`
  background-color: ${color.jctWhite};
  height: auto;
  padding: 9rem 18rem 9rem 0;
  overflow: scroll;
  display: grid;

  @media ${standard.tablet}{
    padding: 7rem 14rem;
    grid: auto-flow / repeat(2, 50%);
    column-gap: 7rem;
  }

  @media ${standard.desktop} {
    column-gap: 12rem;
    grid: auto-flow / repeat(4, 25%);
    padding: 12rem 48rem 0 12rem;

  }

  @media ${retina.laptop} {
    column-gap: 10rem;
    grid: auto-flow / repeat(3, 33%);
    padding: 10rem 30rem 0rem 10rem;
  }
`

const Layout = props => (
  <>

    <MainContainer>
      <CompanyContainer>
        <CompanyName>{props.company_name}</CompanyName>
        <SiteName>{props.site_name}</SiteName>
      </CompanyContainer>
      <SiteDetailsContainer>
        { props.children }
      </SiteDetailsContainer>
    </MainContainer>

    <style jsx global>{`
      *{
        margin: 0;
        padding: 0;
        box-sizing: border-box;
        font-size: 10px;
      }

      body{
        background-color: #292929;
      }

      #__next{
        height: 100%;
        width: 100%;
        display: flex;
        flex-flow: column;
        align-items: center;
        justify-content: center;
      }
    `}</style>

  </>
)

export default Layout
