import styled from 'styled-components'
import { standard, retina, color } from '../public/screensizes'

const MainContainer = styled.main`
  margin: 0;
  padding: 0;
  width: 100%;
  height: 100vh;
  display: flex;
  flex-flow: column;
`

const CompanyContainer = styled.div`
  margin: 3rem 0;
  display: flex;
  flex-flow: column;
  justify-content: center;
  margin-left: 2rem;
`

const CompanyName = styled.h1`
  font-size: 3rem;
  font-weight: 600;
  color: ${color.jctWhite};
  font-family: 'Industry';
`


const SiteName = styled.h2`
  font-size: 2rem;
  font-weight: 600;
  color: #FF7E26;
  font-family: 'Industry';
`


const SiteDetailsContainer = styled.div`
  // background-color: ${color.jctWhite};
  // padding: 3rem 3rem 4rem 3rem;
`


const MobileLayout = props => (
  <>

    <MainContainer>
      <CompanyContainer>
        <CompanyName>{props.company_name}</CompanyName>
        <SiteName>{props.site_name}</SiteName>
      </CompanyContainer>
      <SiteDetailsContainer>
        { props.children }
      </SiteDetailsContainer>
    </MainContainer>

    <style jsx global>{`
      *{
        margin: 0;
        padding: 0;
        box-sizing: border-box;
        font-size: 10px;
        font-family: sans-serif;
        font-weight: 100;
      }

      body{
        background-color: #292929;
      }

      #__next{
        height: 100%;
        width: 100%;
        display: flex;
        flex-flow: column;
        align-items: center;
        justify-content: center;
      }
    `}</style>

  </>
)

export default MobileLayout
