import { Doughnut } from 'react-chartjs-2'

import styled from 'styled-components'
import { color } from '../public/screensizes'


const DoughnutChart = props => {

  const data = {
    datasets: [{
      data: [props.currentValue, (props.totalValue - props.currentValue)],
      backgroundColor: [ color.jctOrange, 'rgba(0,0,0, .05)' ],
      borderWidth: 0
    }]
  }

  return (
    <GraphContainer>
      <Graph>
        <Doughnut 
          data={ data }
          width={ 150 }
          height={ 150 }
          options={{ 
            cutoutPercentage: 80, 
            rotation: (-1.25 * Math.PI),
            circumference: (1.5 * Math.PI),
            tooltips: false
          }}
        />
      </Graph>
      <CurrentValue>{props.currentValue}</CurrentValue>
    </GraphContainer>
  )
}

export default DoughnutChart    


const GraphContainer = styled.div`
  display: grid;
  grid: 1fr / 1fr;
  place-items: center;
`

const Graph = styled.div`
  grid-row: 1;
  grid-column: 1;
`

const CurrentValue = styled.div`
  grid-row: 1;
  grid-column: 1;
  font-family: 'Industry';
  font-size: 3rem;
  color: color.jctDarkGrey;
  font-weight: bold;
`
