import styled from 'styled-components'
import fetch from 'isomorphic-unfetch'

import Layout from '../../components/Layout'
import MobileLayout from '../../components/MobileLayout'
import Details from '../../components/Details'


const Home = ({ data }) => (
  <Layout company_name={"Mammoth"} site_name={"University"}>
    <Details 
      name="University Water Tank" 
      currentValue={data[0].D0}
      totalValue={16}
      tPumpStart={data[0].D1}
      tPumpStop={data[0].D2}
      alarmOn={data[0].D3}
      alarmOff={data[0].D4}
    />
      {console.log(props)}
    <Details 
      name="CEH Water Tank" 
      currentValue={data[0].D5}
      totalValue={16}
      tPumpStart={data[0].D6}
      tPumpStop={data[0].D7}
      alarmOn={data[0].D8}
      alarmOff={data[0].D9}
    />
  </Layout>

)

export async function getServerSideProps(ctx){
  const res = await fetch('http://jct-systems.com/api/university')
  const data = await res.json();

  let isMobileView = ctx.req.headers['user-agent'].search(/Android|iPhone/i)

    if(isMobileView < 0){
      return {
        props: { data, isMobileView: false }
      }
    } else {
      return {
        isMobileView: true,
        props: { data, isMobileView: true }
      }
    }
} 


export default Home
