import styled from 'styled-components'

const MainContainer = styled.main`
  margin: 0;
  padding: 0;
  width: 50%;
  height: 100vh;
  display: flex;
  background-color: #fefefe;  
`

const CompanyContainer = styled.div`
  margin-bottom: 10rem;
`

const SiteDetailsContainer = styled.div`

`



const CompanyName = styled.h1`
  font-size: 2rem;
  align-self: start;
`

const Layout = props => (
  <>
    <MainContainer>
      <CompanyContainer>{props.company_name}</CompanyContainer>

      { props.children }
    </MainContainer>

    <style jsx global>{`
      *{
        margin: 0;
        padding: 0;
        box-sizing: border-box;
        font-size: 10px;
      }

      body{
        background-color: #292929;
      }

      #__next{
        height: 100%;
        width: 100%;
        display: flex;
        flex-flow: column;
        align-items: center;
        justify-content: center;
      }
    `}</style>
  </>
)

export default Layout
