import styled from 'styled-components'
import { standard, retina } from '../public/screensizes'

const MainContainer = styled.main`
  margin: 0;
  padding: 0;
  width: 50%;
  height: 100vh;
  display: flex;
  flex-flow: column;
`

const CompanyContainer = styled.div`
  height: 10%;
  display: flex;
  flex-flow: column;
  justify-content: center;
  margin-left: 1rem;


`

const CompanyName = styled.h1`
  font-size: 6rem;
  font-weight: 600;
  color: #efefef;
  font-family: 'Industry';

  @media ${standard.desktop} {
    font-size: 6rem;
    font-weight: 600;
    color: #efefef;
    font-family: 'Industry';
  }

  @media ${retina.laptop} {
    font-size: 4rem;
    font-weight: 600;
    color: #efefef;
    font-family: 'Industry';
  }
`

const SiteName = styled.h2`
  font-size: 4rem;
  font-weight: 600;
  color: #FF7E26;
  font-family: 'Industry';

  @media ${standard.desktop} {
    font-size: 4rem;
    font-weight: 600;
    color: #FF7E26;
    font-family: 'Industry';
  }

  @media ${retina.laptop} {
    font-size: 2.75rem;
    font-weight: 600;
    color: #FF7E26;
    font-family: 'Industry';
  }
`

const SiteDetailsContainer = styled.div`
  background-color: #efefef;
  width: 100%;
  height: 100%;
  padding: 9rem 9rem 0 9rem;
`

const Layout = props => (
  <>

    <MainContainer>
      <CompanyContainer>
        <CompanyName>{props.company_name}</CompanyName>
        <SiteName>{props.site_name}</SiteName>
      </CompanyContainer>
      <SiteDetailsContainer>
        { props.children }
      </SiteDetailsContainer>
    </MainContainer>

    <style jsx global>{`
      *{
        margin: 0;
        padding: 0;
        box-sizing: border-box;
        font-size: 10px;
        font-family: sans-serif;
        font-weight: 100;
      }

      body{
        background-color: #292929;
      }

      #__next{
        height: 100%;
        width: 100%;
        display: flex;
        flex-flow: column;
        align-items: center;
        justify-content: center;
      }
    `}</style>

  </>
)

export default Layout
