import styled from 'styled-components'
import { standard, retina, color } from '../public/screensizes'
import "react-multi-carousel/lib/styles.css";
import Carousel from 'react-multi-carousel'
import Details from '../components/Details'

const responsive = {
  mobile: {
    breakpoint: { max: 424, min: 0 },
    items: 1
  }
}

const CustomDot = ({ onClick, ...rest }) => {
  const { onMove, index, active, carouselState: { currentSlide, deviceType }} = rest;

  return(
    active ? <ActiveDot /> : <InactiveDot />
  )
}

const CustomItem = ({ onClick, ...rest }) => {
  const { onMove, index, active, carouselState: { currentSlide, deviceType }} = rest;

  return(
    <ItemContainer />
  )
}

const MobileCarousel = props => (
  <Carousel 
    responsive={responsive}
    swipeable
    showDots
    arrows={false}
    customDot={ <CustomDot /> }
    // itemClass={ <ItemContainer /> }
    itemClass={{ backgroundColor: 'blue' } }
  >
    <Details name="Oil Tank 1" />
    <Details name="Oil Tank 2" />
    <Details name="Water Tank 1" />
    <Details name="Water Tank 2" />
  </Carousel>
)

export default MobileCarousel



const ActiveDot = styled.button`
  height: 10px;
  width: 10px;
  margin-right: 5px;
  border-radius: 100%;
  border: none;
  background-color: ${color.jctOrange};
  position: static;
`

  const InactiveDot = styled.button`
  height: 10px;
  width: 10px;
  margin-right: 5px;
  border-radius: 100%;
  border: none;
  background-color: ${color.jctDarkGray};
`

const ItemContainer = styled.li`
  background-color: pink;
  height: 100%;
  width: 10px;
`
