import fetch from 'isomorphic-unfetch'
import Link from 'next/link'
import { isBrowser, isMobile } from 'react-device-detect'

import NewLayout from '../../../components/NewLayout'
import NewDetails from '../../../components/NewDetails'
import DoughnutChart from '../../../components/DoughnutChart'

import NewMobileLayout from '../../../components/NewMobileLayout'
import NewMobileDetails from '../../../components/NewMobileDetails'
import NewMobileDoughtnutChart from '../../../components/NewMobile'


const Home = ({ data, sites }) => (      

  isBrowser ?
    <NewLayout 
      company={'mammoth'}
      sites={ sites.map((site, i) => <li key={i}><Link href={`/mammoth/${site.BATTERY.toLowerCase()}`}><a>{site.BATTERY}</a></Link></li>) } 
      site={'CEH'} 
      date={data[0].mDate} 
      time={data[0].mTime}
      >
        <NewDetails
          resourceType='Water'
          name={data[0].S1} 
          unitOfMeasure={data[0].S2.toLowerCase()}
          currentValue={data[0].V1}
          totalValue={16}
          tpumpOn={data[0].V3}
          tpumpOff={data[0].V4}
          alarmOn={data[0].V5}
          alarmOff={data[0].V6}
        >
          <DoughnutChart name={data[0].S1} currentValue={data[0].V1} totalValue={16} unitOfMeasure={data[0].S2.toLowerCase()} />
          <DoughnutChart name={data[0].S3} currentValue={data[0].V2} totalValue={16} unitOfMeasure={data[0].S4.toLowerCase()} />
        </NewDetails>
      </NewLayout>
    :
      <NewMobileLayout 
        company={'mammoth'}
        sites={ sites.map((site, i) => <li key={i}><Link href={`/mammoth/${site.BATTERY.toLowerCase()}`}><a>{site.BATTERY}</a></Link></li>) } 
        sitename={'CEH'} 
        date={data[0].mDate} 
        time={data[0].mTime}
        >
          <NewMobileDetails
            resourceType='Water'
            name={data[0].S1} 
            unitOfMeasure={data[0].S2.toLowerCase()}
            currentValue={data[0].V1}
            totalValue={16}
            tpumpOn={data[0].V3}
            tpumpOff={data[0].V4}
            alarmOn={data[0].V5}
            alarmOff={data[0].V6}
          >
            <DoughnutChart name={data[0].S1} currentValue={data[0].V1} totalValue={16} unitOfMeasure={data[0].S2.toLowerCase()} />
            <DoughnutChart name={data[0].S3} currentValue={data[0].V2} totalValue={16} unitOfMeasure={data[0].S4.toLowerCase()} />
          </NewMobileDetails>
    </NewMobileLayout>
)

export async function getServerSideProps(ctx){
  const res = await fetch('http://jct-systems.com/api/ceh')
  const res1 = await fetch('http://localhost:3001/api/mammoth')

  const data = await res.json()
  const sites = await res1.json()

  return {
    props: { data, sites }
  }
} 


export default Home
