import styled from 'styled-components'
import { color, standard, retina } from '../public/screensizes'

import Trend from './Trend'
import DoughnutChart from './DoughnutChart'

const DetailsContainer = styled.div`
  display: flex;
  flex-flow: column;
  margin-bottom: 15rem;

  /* @media ${standard.desktop}{
    max-width: 35rem;
    max-height: 35rem;
  }

  @media ${retina.mobile}{
    padding: 2rem 2rem 4rem 2rem;
  } */
`

const NameAlarmsContainer = styled.div`
  display: flex;
  margin-bottom: 4rem;
`

const DetailsName = styled.h1`
  color: #fff;
  font-size: 2.4rem;
  font-weight: bold;
  background-color: ${color.jctDarkGray};
  padding: 1rem 2rem;
  max-width: auto;

  /* @media ${standard.tabletP}{
    font-size: 2rem;
  }

  @media ${standard.tabletL}{
    font-size: 2rem;
  }

  @media ${retina.laptopM}{
    font-size: 3rem;
  } */
`

const DetailsAlarmContainer = styled.p`
  display: flex;
  flex-flow: column;
  border-bottom: 1px solid ${color.jctDarkGray};
  margin-left: 2rem;
`

const AlarmTitle = styled.h2`
  font-size: 1.8rem;
  font-weight: 500;
`

const AlarmOnOff = styled.div`
  font-size: 1.8rem;
`

const AlarmOnOffNum = styled.span`
  font-weight: bold;
  font-size: 1.8rem;
`

const DoughnutChartContainer = styled.div`
  display: flex;
`


const Details = props => (
  <DetailsContainer>
    <NameAlarmsContainer>
      <DetailsName>{props.resourceType}</DetailsName>
      <DetailsAlarmContainer>
        <AlarmTitle>Alarm</AlarmTitle>
        <AlarmOnOff>On: <AlarmOnOffNum>12.00</AlarmOnOffNum> Off: <AlarmOnOffNum>10.00</AlarmOnOffNum></AlarmOnOff>
      </DetailsAlarmContainer>
      <DetailsAlarmContainer>
        <AlarmTitle>T-Pump</AlarmTitle>
        <AlarmOnOff>On: <AlarmOnOffNum>9.00</AlarmOnOffNum> Off: <AlarmOnOffNum>8.00</AlarmOnOffNum></AlarmOnOff>
      </DetailsAlarmContainer>
    </NameAlarmsContainer>

    <DoughnutChartContainer>
      <DoughnutChart name={props.name} currentValue={props.currentValue} totalValue={props.totalValue} unitOfMeasure={props.unitOfMeasure} />
      <DoughnutChart name={props.name} currentValue={props.currentValue} totalValue={props.totalValue} unitOfMeasure={props.unitOfMeasure} />
      <DoughnutChart name={props.name} currentValue={props.currentValue} totalValue={props.totalValue} unitOfMeasure={props.unitOfMeasure} />
      <DoughnutChart name={props.name} currentValue={props.currentValue} totalValue={props.totalValue} unitOfMeasure={props.unitOfMeasure} />
    </DoughnutChartContainer>
  </DetailsContainer>
)

export default Details
