import styled from 'styled-components'
import fetch from 'isomorphic-unfetch'

import Layout from '../../components/Layout'
import MobileLayout from '../../components/MobileLayout'
import Details from '../../components/Details'


const Home = ({ univData, cehData, isMobileView }) => (
  isMobileView ?
    <MobileLayout company_name={"Mammoth"} site_name={"University"}>
      <Details 
        name={univData} 
        currentValue={univData[0].D0}
        totalValue={16}
        tPumpStart={univData[0].D1}
        tPumpStop={univData[0].D2}
        alarmOn={univData[0].D3}
        alarmOff={univData[0].D4}
      />

      <Details 
        name="CEH Tank" 
        currentValue={cehData[0].D5}
        totalValue={16}
        tPumpStart={cehData[0].D6}
        tPumpStop={cehData[0].D7}
        alarmOn={cehData[0].D8}
        alarmOff={cehData[0].D9}
      />
    </MobileLayout>
    :
    <Layout company_name={"Mammoth"} site_name={"University"}>
      <Details 
        name="University Water Tank" 
        currentValue={univData[0].D0}
        totalValue={16}
        tPumpOn={univData[0].D1}
        tPumpOff={univData[0].D2}
        alarmOn={univData[0].D3}
        alarmOff={univData[0].D4}
      />

      <Details 
        name="CEH Water Tank" 
        currentValue={cehData[0].D5}
        totalValue={16}
        tPumpOn={cehData[0].D6}
        tPumpOff={cehData[0].D7}
        alarmOn={cehData[0].D8}
        alarmOff={cehData[0].D9}
      />
    </Layout>
)

export async function getServerSideProps(ctx){
  // const univRes = await fetch('http://jct-systems.com/api/university')
  // const cehRes = await fetch('http://jct-systems.com/api/ceh')

  const univRes = await fetch('http://localhost:3001/api/university')
  const cehRes = await fetch('http://localhost:3001/api/ceh')

  const univData = await univRes.json();
  const cehData = await cehRes.json();

  let isMobileView = ctx.req.headers['user-agent'].search(/Android|iPhone/i)

    if(isMobileView < 0){
      return {
        props: { univData, cehData, isMobileView: false }
      }
    } else {
      return {
        props: { univData, cehData, isMobileView: true }
      }
    }
} 


export default Home
