import styled from 'styled-components'
import { color, standard, retina } from '../public/screensizes'

import Trend from './Trend'
import DoughnutChart from './DoughnutChart'

const DetailsContainer = styled.div`
  display: flex;
  flex-flow: column;

  /* @media ${standard.desktop}{
    max-width: 35rem;
    max-height: 35rem;
  }

  @media ${retina.mobile}{
    padding: 2rem 2rem 4rem 2rem;
  } */
`

const NameAlarmsContainer = styled.div`
  display: flex;
`

const DetailsName = styled.h1`
  color: #fff;
  font-size: 2.4rem;
  font-weight: bold;
  margin-bottom: 1rem;
  background-color: ${color.jctDarkGray};
  padding: 1rem 2rem;
  max-width: auto;

  /* @media ${standard.tabletP}{
    font-size: 2rem;
  }

  @media ${standard.tabletL}{
    font-size: 2rem;
  }

  @media ${retina.laptopM}{
    font-size: 3rem;
  } */
`

const DetailsAlarmContainer = styled.p`
  display: flex;
  flex-flow: column;
`

const AlarmTitle = styled.h2`
font-size: 1.8rem;

`

const AlarmOnOff = styled.span`

`


const Details = props => (
  <DetailsContainer>
    <NameAlarmsContainer>
      <DetailsName>{props.name}</DetailsName>
      <DetailsAlarmContainer>
        <AlarmTitle>Alarm</AlarmTitle>
        <AlarmOnOff>On: {props.alarmOn} Off: {props.alarmOff}</AlarmOnOff>
      </DetailsAlarmContainer>
    </NameAlarmsContainer>
    {/* <DoughnutChart currentValue={props.currentValue} totalValue={props.totalValue} unitOfMeasure={props.unitOfMeasure} /> */}
  </DetailsContainer>
)

export default Details
