export default class Sidebar extends React.Component{
  render(){
    return(
      <SidebarContainer>
        <CompanyContainer>
          <img src="/static/jct-logo.png" />
        </CompanyContainer>
        <SiteNavContainer>
          <h1>Sites</h1>
          <SiteLinks>
          </SiteLinks>
        </SiteNavContainer>
      </SidebarContainer>
    )
  }
}
