import styled from 'styled-components'
import { standard, retina, color } from '../public/screensizes'
import Link from 'next/link'

const MainContainer = styled.main`
  height: 100vh;
  width: 100vw;
  display: flex;
  flex-flow: row;
`

const Sidebar = styled.div`
  width: 30rem;
  background-color: ${color.jctDarkGray};
  display: grid;
  grid-template-rows: 12.5rem 1fr;
  position: sticky;
`

const CompanyContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  height: 12.5rem;

  img{
    height: 75%;
  }
`

const SiteNavContainer = styled.div`
  width: 100%;
  height: 100%;
  padding-left: 3rem;

  h1{
    color: #fff;
    font-size: 18px;
    font-weight: 500;
  }
`

const SiteLinks = styled.nav`
  ul{
    list-style: none;
    margin-top: 1.5rem;
  }

  li{
    height: 5rem;
    display: flex;
    align-items: center;
    padding-left: 2rem;
    margin-bottom: 1rem;
  }

  li:hover{
    background-color: ${color.jctOrange};
  }

  a{
    color: #fff;
    text-decoration: none;
    font-size: 16px;
  }
`
const SiteDetailsContainer = styled.div`
  padding: 9rem 18rem 9rem 0;
  overflow: scroll;
  display: grid;
  grid-template-rows: 
`

const SiteName = styled.h2`
  font-weight: 600;
  color: #FF7E26;
  margin-bottom: .5rem;
`

const DateTime = styled.h3`
  color: rgba(255,255,255,.5);
  font-size: 1.75rem;
  font-weight: 400;
  margin-left: .25rem;
`


const Layout = props => (
  <MainContainer>
    <Sidebar>
      <CompanyContainer>
        <img src="/static/mammoth-logo-light.png" />
      </CompanyContainer>
      <SiteNavContainer>
        <h1>Sites</h1>
        <SiteLinks>
          <ul>
            <li>
              <Link href="jctbty"><a>BTY</a></Link>
            </li>
            <li>
              <Link href="north"><a>North</a></Link>
            </li>
          </ul>
        </SiteLinks>
      </SiteNavContainer>
    </Sidebar>
    <SiteDetailsContainer>
      <SiteName>{props.site_name}</SiteName>
      <DateTime>{props.date} - {props.time}</DateTime>
      {/* { props.children } */}
    </SiteDetailsContainer>
  </MainContainer>
)

export default Layout
