import styled from 'styled-components'
import fetch from 'isomorphic-unfetch'
import Link from 'next/link'

import NewLayout from '../../components/NewLayout'

const Container = styled.div`
  display: flex;
  width: 100vw;
  height: 100vh;
`

const LogoSide = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: #292929;
  width: 50%;
`

const LoginSide = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 50%;
  flex-flow: column;
`

const SiteHeader = styled.div`
  height: 4rem;
  width: 10rem;
  background-color: #292929;
  color: #eee;
  font-size: 2.4rem;
  font-weight: bold;
  display: flex;
  align-items: center;
  justify-content: center;
`

const SiteList = styled.ul`
  list-style: none;
  margin-top: 2rem;

  li{
    font-size: 1.8rem;
    text-align: center;
    height: 5rem;
    width: 20rem;
  }

  li:hover{
    background-color: #ff7e26;
  }
`



const Home = ({ data }) => (
  <Container>
    <LogoSide>
      <img src="../static/mammoth-logo.png" />
    </LogoSide>
    <LoginSide>
      <SiteHeader>Sites</SiteHeader>
      <SiteList>
        {
          data.map((d, i) => (
            <li key={i}>{d.BATTERY}</li>
          ))
        }
      </SiteList>
    </LoginSide>
  </Container>
)

export async function getServerSideProps(ctx){
  const res = await fetch('http://localhost:3001/api/mammoth')
  const data = await res.json();

  return {
    props: { data }
  }
} 


export default Home
