import fetch from 'isomorphic-unfetch'
import Link from 'next/link'
import { BrowserView, MobileView } from 'react-device-detect'

import NewLayout from '../../../components/NewLayout'
import NewDetails from '../../../components/NewDetails'
import DoughnutChart from '../../../components/DoughnutChart'

import NewMobileLayout from '../../../components/NewMobileLayout'
import NewMobileDetails from '../../../components/NewMobileDetails'
import NewMobileDoughnutChart from '../../../components/NewMobileDoughnutChart'


const Home = ({ data, sites, isMobileView }) => (
  <>
  <BrowserView>
    <NewLayout 
      company='mammoth'
      sites={ sites.map((site, i) => <li key={i}><Link href={`/mammoth/${site.BATTERY.toLowerCase()}`}><a>{site.BATTERY}</a></Link></li>) } 
      sitename={"University"} 
      date={data[0].mDate} 
      time={data[0].mTime}
    >
      <NewDetails
        resourceType='Water'
        name={data[0].S1} 
        unitOfMeasure={data[0].S2.toLowerCase()}
        currentValue={data[0].V1}
        totalValue={16}
        tpumpOn={data[0].V3}
        tpumpOff={data[0].V4}
        alarmOn={data[0].V5}
        alarmOff={data[0].V6}
      >
        <DoughnutChart name={data[0].S1} currentValue={data[0].V1} totalValue={16} unitOfMeasure={data[0].S2.toLowerCase()} />
        <DoughnutChart name={data[0].S3} currentValue={data[0].V2} totalValue={16} unitOfMeasure={data[0].S4.toLowerCase()} />
      </NewDetails>
    </NewLayout>
  </BrowserView>

  <MobileView>
    <NewMobileLayout 
      company='mammoth'
      sites={ sites.map((site, i) => <li key={i}><Link href={`/mammoth/${site.BATTERY.toLowerCase()}`}><a>{site.BATTERY}</a></Link></li>) } 
      siten={"University"} 
      date={data[0].mDate} 
      time={data[0].mTime}
    >
      <NewMobileDetails
        resourceType='Water'
        name={data[0].S1} 
        unitOfMeasure={data[0].S2.toLowerCase()}
        currentValue={data[0].V1}
        totalValue={16}
        tpumpOn={data[0].V3}
        tpumpOff={data[0].V4}
        alarmOn={data[0].V5}
        alarmOff={data[0].V6}
      >
        <NewMobileDoughnutChart name={data[0].S1} currentValue={data[0].V1} totalValue={16} unitOfMeasure={data[0].S2.toLowerCase()} />
        <NewMobileDoughnutChart name={data[0].S3} currentValue={data[0].V2} totalValue={16} unitOfMeasure={data[0].S4.toLowerCase()} />
      </NewMobileDetails>
    </NewMobileLayout>
  </MobileView>
  </>
)

export async function getServerSideProps(ctx){
  const res = await fetch('http://jct-systems.com/api/university')
  const data = await res.json();

  const res1 = await fetch('http://localhost:3001/api/mammoth')
  const sites = await res1.json();

  let isMobileView = ctx.req.headers['user-agent'].search(/Android|iPhone/i)

  if(isMobileView < 0){
    return {
      props: { data, sites, isMobileView: false }
    }
  } else {
    return {
      props: { data, sites, isMobileView: true }
    }
  }
} 


export default Home
