import React, { useState, useContext } from 'react'

const CompanyStateContext = React.createContext()
const CompanySetStateContext = React.createContext()

export const CompanyProvider = ({ children }) => {
  const { companyList, setCompanyList } = useState('hello')

  return(
    <CompanySetStateContext.Provider>
      <CompanyStateContext.Provider value={companyList}>
        { children }
      </CompanyStateContext.Provider>
    </CompanySetStateContext.Provider>
  )
}

export const useCompany = () => useContext(CompanyListContext)
