import fetch from 'isomorphic-unfetch'
import Link from 'next/link'
import { BrowserView, MobileView } from 'react-device-detect'

import BrowserLayout from '../../../components/Layout'
import BrowserDetails from '../../../components/Details'
import BrowserGaugeChart from '../../../components/GaugeChart'

import MobileLayout from '../../../components/Layout'
import MobileDetails from '../../../components/Details'
import NewMobileGaugeChart from '../../../components/GaugeChart'


const University = ({ data, sites }) => (
  <>
  <BrowserView>
    <NewLayout 
      company='mammoth'
      sites={ sites.map((site, i) => <li key={i}><Link href={`/mammoth/${site.BATTERY.toLowerCase()}`}><a>{site.BATTERY}</a></Link></li>) } 
      sitename={"University"} 
      date={data[0].mDate} 
      time={data[0].mTime}
    >
      <NewDetails
        resourceType='Water'
        name={data[0].S1} 
        unitOfMeasure={data[0].S2.toLowerCase()}
        currentValue={data[0].V1}
        totalValue={16}
        tpumpOn={data[0].V3}
        tpumpOff={data[0].V4}
        alarmOn={data[0].V5}
        alarmOff={data[0].V6}
      >
        <GaugeChart name={data[0].S1} currentValue={data[0].V1} totalValue={16} unitOfMeasure={data[0].S2.toLowerCase()} />
        <GaugeChart name={data[0].S3} currentValue={data[0].V2} totalValue={16} unitOfMeasure={data[0].S4.toLowerCase()} />
      </NewDetails>
    </NewLayout>
  </BrowserView>

  <MobileView>
    <NewMobileLayout 
      company='mammoth'
      sites={ sites.map((site, i) => <li key={i}><Link href={`/mammoth/${site.BATTERY.toLowerCase()}`}><a>{site.BATTERY}</a></Link></li>) } 
      sitename={"University"} 
      date={data[0].mDate} 
      time={data[0].mTime}
    >
      <NewMobileDetails
        resourceType='Water'
        name={data[0].S1} 
        unitOfMeasure={data[0].S2.toLowerCase()}
        currentValue={data[0].V1}
        totalValue={16}
        tpumpOn={data[0].V3}
        tpumpOff={data[0].V4}
        alarmOn={data[0].V5}
        alarmOff={data[0].V6}
      >
        <NewMobileGaugeChart name={data[0].S1} currentValue={data[0].V1} totalValue={16} unitOfMeasure={data[0].S2.toLowerCase()} />
        <NewMobileGaugeChart name={data[0].S3} currentValue={data[0].V2} totalValue={16} unitOfMeasure={data[0].S4.toLowerCase()} />
      </NewMobileDetails>
    </NewMobileLayout>
  </MobileView>
  </>
)

export async function getServerSideProps(ctx){
  const res = await fetch('http://jct-systems.com/api/university')
  const data = await res.json();

  const res1 = await fetch('http://localhost:3001/api/mammoth')
  const sites = await res1.json();

  return {
    props: { data, sites }
  }
} 


export default University
