import React, { useState, useContext } from 'react'

const CompanyListContext = React.createContext()

export const CompanyContext = ({ children }) => {
  const { companyList, setCompanyList } = useState([])

  return(
    <CompanyListContext.Provider value={companyList}>
      { children }
    </CompanyListContext.Provider>
  )
}

export const useCompanyList = () => useContext(CompanyListContext)
