import fetch from 'isomorphic-unfetch'
import Link from 'next/link'
import NewLayout from '../../components/NewLayout'
import { useCompany, setCompany } from '../../components/CompanyProvider'
import { useEffect } from 'react'

const Start = ({ siteList, companyName }) => {
  const dispatch = setCompany()
  const sites = useCompany()

  useEffect(() => {
    const companies = siteList.map(comp => comp)
    dispatch({ 
      type: 'SET', 
      payload: [companies, companyName]
    })
  }, [])

