import styled from 'styled-components'
import fetch from 'isomorphic-unfetch'

import Layout from '../../components/Layout'
import MobileLayout from '../../components/MobileLayout'
import Details from '../../components/Details'


const Home = ({ cehData, isMobileView }) => (
  isMobileView ?
    <MobileLayout company_name={"Mammoth"} site_name={"CEH"}>
      <Details 
        name={cehData[0].S1} 
        currentValue={cehData[0].V1}
        totalValue={16}
        unitOfMeasure={cehData[0].S2.toLowerCase()}
        tPumpOn={cehData[0].V3}
        tPumpOff={cehData[0].V4}
        alarmOn={cehData[0].V5}
        alarmOff={cehData[0].V6}
      />
    </MobileLayout>
    :
    <Layout company_name={"Mammoth"} site_name={"CEH"}>
      <Details 
        name={cehData[0].S1} 
        currentValue={cehData[0].V1}
        totalValue={16}
        unitOfMeasure={cehData[0].S2.toLowerCase()}
        // tPumpOn={cehData[0].V3}
        tPumpOff={cehData[0].V4}
        alarmOn={cehData[0].V5}
        alarmOff={cehData[0].V6}
      />
    </Layout>
)

export async function getServerSideProps(ctx){
  // const univRes = await fetch('http://jct-systems.com/api/university')
  // const cehRes = await fetch('http://jct-systems.com/api/ceh')

  const cehRes = await fetch('http://localhost:3001/api/ceh')

  const cehData = await cehRes.json();

  let isMobileView = ctx.req.headers['user-agent'].search(/Android|iPhone/i)

  if(isMobileView < 0){
    return {
      props: { cehData, isMobileView: false }
    }
  } else {
    return {
      props: { cehData, isMobileView: true }
    }
  }
} 


export default Home
