import styled from 'styled-components'
import fetch from 'isomorphic-unfetch'
import Link from 'next/link'

import NewLayout from '../../components/NewLayout'
import NewMobileLayout from '../../components/NewMobileLayout'
import NewDetails from '../../components/NewDetails'
import DoughnutChart from '../../components/DoughnutChart'
// import Layout from '../../../components/Layout'
// import MobileLayout from '../../../components/MobileLayout'
// import MobileCarousel from '../../../components/MobileCarousel'
// import FullDetails from '../../../components/FullDetails'
// import Details from '../../../components/Details'


const Home = ({ data, isMobileView }) => (
  isMobileView ?
  <NewMobileLayout site_name='University' date='01/01/2020' time='5:20 PM'>
    <NewDetails resourceType='Water' alarmOn={12} alarmOff={10}>
      <DoughnutChart name={'Tank 1'} currentValue={9.6} totalValue={16} unitOfMeasure={data[0].S2.toLowerCase()} />
    </NewDetails>
  </NewMobileLayout>
  :
  <NewLayout
    company="jct" 
    sites={
      data.map((site, i) => (
        <li key={i}>
          <Link href={`jct/${site.BATTERY.toLowerCase()}`}><a>{site.BATTERY}</a></Link>
        </li>
      ))
    }
  />
)

export async function getServerSideProps(ctx){
  const res = await fetch('http://localhost:3001/api/jct')
  const data = await res.json();

  let isMobileView = ctx.req.headers['user-agent'].search(/Android|iPhone/i)

    if(isMobileView < 0){
      return {
        props: {
          isMobileView: false,
          data
        }
      }
    } else {
      return {
        props: {
          isMobileView: true,
          data
        }
      }
    }
} 


export default Home
