import styled from 'styled-components'
import { standard, retina, color } from '../public/screensizes'

const MainContainer = styled.main`
  height: 100vh;
  width: 100vw;
  display: flex;
  flex-flow: row;
`

const Sidebar = styled.div`
  width: 30rem;
  background-color: ${color.jctDarkGray};
`

const CompanyContainer = styled.div`
  display: flex;
  flex-flow: column;
  justify-content: center;
  margin-left: 1rem;

  &img{
    height: 50%;
  }
`

const CompanyName = styled.h1`
  font-weight: 600;
  color: #efefef;
`

const SiteName = styled.h2`
  font-weight: 600;
  color: #FF7E26;
  margin-bottom: .5rem;
`

const DateTime = styled.h3`
  color: rgba(255,255,255,.5);
  font-size: 1.75rem;
  font-weight: 400;
  margin-left: .25rem;
`

const SiteDetailsContainer = styled.div`
  background-color: ${color.jctWhite};
  height: auto;
  padding: 9rem 18rem 9rem 0;
  overflow: scroll;
  display: grid;
`

const Layout = props => (
  <MainContainer>
    <Sidebar>
      <CompanyContainer>
        <img src="/static/mammoth-logo.png" />
      </CompanyContainer>
    </Sidebar>
    {/* <CompanyContainer>
      <SiteName>{props.site_name}</SiteName>
      <DateTime>{props.date} - {props.time}</DateTime>
    </CompanyContainer>
    <SiteDetailsContainer>
      { props.children }
    </SiteDetailsContainer> */}
  </MainContainer>
)

export default Layout
