import styled from 'styled-components'
import { standard, retina, color } from '../public/screensizes'
import Link from 'next/link'
import Sidebar from './Sidebar'
import { useRouter } from 'next/router'
import { useState, useEffect } from 'react'


const MainContainer = styled.main`
  height: 100vh;
  width: 100vw;
  display: flex;
  flex-flow: row;
`

const SiteDetailsContainer = styled.div`
  padding-left: 7rem;
  overflow: scroll;
  display: grid;
  grid-template-rows: 20rem 1fr;
`

const SiteNameTime = styled.div`
  height: 15rem;
  display: flex;
  flex-flow: column;
  justify-content: center;
`

const SiteName = styled.h2`
  font-weight: 600;
  color: ${color.jctDarkGray};
  margin-bottom: .5rem;
  font-size: 36px;
  text-transform: uppercase;
`

const DateTime = styled.h3`
  color: ${color.jctDarkGray};
  font-size: 1.75rem;
  font-weight: 400;
  margin-left: .25rem;
`

const SiteDetails = styled.div`
  /* background-color: pink; */
`


const Layout = props => {
  const router = useRouter()
  const { name } = router.query
  const [compList, setCompList] = useState()

  // useEffect(() => {
  //   const getData = async () => {
  //     const res = await fetch(`http://localhost:3001/api/${name}`)
  //     const json = await res.json()

  //     return json
  //   }
  //   setCompList(getData())
  // }, [])

  return(
  <MainContainer>
    <Sidebar sites={props.sites} company={props.company} />
    <SiteDetailsContainer>
      <SiteNameTime>
        <SiteName>{props.sitename}</SiteName>
        <DateTime>{props.data} - {props.time}</DateTime>
      </SiteNameTime>
      <SiteDetails>
        { props.children }
      </SiteDetails>
    </SiteDetailsContainer>
  </MainContainer>
  )
}

export async function getServerSideProps(ctx){
  const { name } = ctx.query
  const res = await fetch(`http://localhost:3001/api/${name}`)
  const data = await res.json();
  console.log(data);
  return {
    props: { siteList: data, companyName: name}
  }
} 

export default Layout
