import styled from 'styled-components'
import { standard, retina, color } from '../public/screensizes'
import Link from 'next/link'

const MainContainer = styled.main`
  height: 100vh;
  width: 100vw;
  display: flex;
  flex-flow: row;
`

const Sidebar = styled.div`
  width: 30rem;
  background-color: ${color.jctDarkGray};
  display: grid;
  grid-template-rows: 12.5rem 1fr;
  position: sticky;
`

const CompanyContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  height: 12.5rem;

  img{
    height: 75%;
  }
`

const SiteNavContainer = styled.div`
  width: 100%;
  height: 100%;
  padding-left: 3rem;

  h1{
    color: #fff;
    font-size: 18px;
    font-weight: 500;
  }
`

const SiteLinks = styled.nav`
  ul{
    list-style: none;
    /* margin-top: 1.5rem; */
  }

  li{
    height: 5rem;
    display: flex;
    align-items: center;
    padding-left: 2rem;
    margin-bottom: 1rem;
  }

  li:hover{
    background-color: ${color.jctOrange};
  }

  a{
    color: #fff;
    text-decoration: none;
    font-size: 16px;
  }
`
const SiteDetailsContainer = styled.div`
  padding-left: 7rem;
  overflow: scroll;
  display: grid;
  grid-template-rows: 15rem 1fr;
  width: 100%;
`

const SiteNameTime = styled.div`
  height: 15rem;
  display: flex;
  flex-flow: column;
  justify-content: center;
`

const SiteName = styled.h2`
  font-weight: 600;
  color: ${color.jctDarkGray};
  margin-bottom: .5rem;
  font-size: 36px;
  text-transform: uppercase;
`

const DateTime = styled.h3`
  color: ${color.jctDarkGray};
  font-size: 1.75rem;
  font-weight: 400;
  margin-left: .25rem;
`

const SiteDetails = styled.div`
  background-color: pink;
`


const Layout = props => (
  <MainContainer>
    <Sidebar>
      <CompanyContainer>
        <img src="/static/jct-logo.png" />
      </CompanyContainer>
      <SiteNavContainer>
        <h1>Sites</h1>
        <SiteLinks>
          <ul>
            <li>
              <Link href="jctbty"><a>BTY</a></Link>
            </li>
            <li>
              <Link href="north"><a>North</a></Link>
            </li>
          </ul>
        </SiteLinks>
      </SiteNavContainer>
    </Sidebar>
    <SiteDetailsContainer>
      <SiteNameTime>
        <SiteName>BTY</SiteName>
        <DateTime>5/20/2020 - 2:32 PM</DateTime>
      </SiteNameTime>
      {/* <SiteName>{props.site_name}</SiteName>
      <DateTime>{props.date} - {props.time}</DateTime> */}
      {/* { props.children } */}
      <SiteDetails>
        { props.children }
      </SiteDetails>
    </SiteDetailsContainer>
  </MainContainer>
)

export default Layout
