import styled from 'styled-components'
import fetch from 'isomorphic-unfetch'
import Link from 'next/link'
import { BrowserView, MobileView } from 'react-device-detect'

import Layout from '../../../components/Layout'
import NewLayout from '../../../components/NewLayout'
import NewMobileLayout from '../../../components/NewMobileLayout'
import MobileLayout from '../../../components/MobileLayout'
import MobileCarousel from '../../../components/MobileCarousel'
import FullDetails from '../../../components/FullDetails'
import Details from '../../../components/Details'
import NewDetails from '../../../components/NewDetails'
import NewMobileDetails from '../../../components/NewMobileDetails'
import DoughnutChart from '../../../components/DoughnutChart'
import Table from '../../../components/Table'


const TableRow = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  padding: 1.5rem;

  &:nth-child(even){
    background-color: rgba(0,0,0,.1);
  }
`

const CellHeader = styled.span`
  font-size: 1.4rem;
  font-weight: bold;
`

const CellData = styled.span`
  font-size: 1.4rem;
  justify-self: end;
`



const North = ({ data, sites }) => (

  <NewMobileLayout
    sites={ sites.map((site, i) => <li key={i}><Link href={`/jct/${site.BATTERY.toLowerCase()}`}><a>{site.BATTERY}</a></Link></li>) } 
    company={"jct"} 
    sitename={"North"} 
    date={data[0].mDate} 
    time={data[0].mTime}
  >
    <NewMobileDetails resourceType='PSI'>
      <Table>
        <TableRow>
          <CellHeader></CellHeader>
        </TableRow>
      </Table>
    </NewMobileDetails>
  </NewMobileLayout>
)

export async function getServerSideProps(ctx){
  const res1 = await fetch('http://localhost:3001/api/mammoth')
  const res = await fetch('http://localhost:3001/api/mammoth/mpc')

  const sites = await res1.json();
  const data = await res.json();

  return {
    props: { data, sites }
  }
} 


export default North
