import fetch from 'isomorphic-unfetch'
import Link from 'next/link'
import { isMobile } from 'react-device-detect'

import BrowserLayout from '../../../components/browser/Layout'
import NewDetails from '../../../components/NewDetails'
import GaugeChart from '../../../components/browser/GaugeChart'

import MobileLayout from '../../../components/mobile/Layout'
import MobileDetails from '../../../components/mobile/Details'
import NewMobileDoughnutChart from '../../../components/mobile/GaugeChart'


const Home = ({ data, sites }) => (
  isMobile ?
    <MobileLayout 
      sites={ sites.map((site, i) => <li key={i}><Link href={`/mammoth/${site.BATTERY.toLowerCase()}`}><a>{site.BATTERY}</a></Link></li>) } 
      company={'mammoth'}
      sitename={'CEH'} 
      date={data[0].mDate} 
      time={data[0].mTime}
    >
      <MobileDetails
      resourceType='Water'
      name={data[0].S1} 
      unitOfMeasure={data[0].S2.toLowerCase()}
      currentValue={data[0].V1}
      totalValue={16}
      tpumpOn={data[0].V3}
      tpumpOff={data[0].V4}
      alarmOn={data[0].V5}
      alarmOff={data[0].V6}
      >
        <GaugeChart name={data[0].S1} currentValue={data[0].V1} totalValue={16} unitOfMeasure={data[0].S2.toLowerCase()} />
        <GaugeChart name={data[0].S3} currentValue={data[0].V2} totalValue={16} unitOfMeasure={data[0].S4.toLowerCase()} />
      </MobileDetails>
    </MobileLayout>
  :
    <Layout 
      sites={ sites.map((site, i) => <li key={i}><Link href={`/mammoth/${site.BATTERY.toLowerCase()}`}><a>{site.BATTERY}</a></Link></li>) } 
      company={'mammoth'}
      sitename={'CEH'} 
      date={data[0].mDate} 
      time={data[0].mTime}
    >
      <NewDetails
      resourceType='Water'
      name={data[0].S1} 
      unitOfMeasure={data[0].S2.toLowerCase()}
      currentValue={data[0].V1}
      totalValue={16}
      tpumpOn={data[0].V3}
      tpumpOff={data[0].V4}
      alarmOn={data[0].V5}
      alarmOff={data[0].V6}
      >
        <GaugeChart name={data[0].S1} currentValue={data[0].V1} totalValue={16} unitOfMeasure={data[0].S2.toLowerCase()} />
        <GaugeChart name={data[0].S3} currentValue={data[0].V2} totalValue={16} unitOfMeasure={data[0].S4.toLowerCase()} />
      </NewDetails>
    </Layout>
)

export async function getServerSideProps(ctx){
  const res = await fetch('http://jct-systems.com/api/ceh')
  const res1 = await fetch('http://localhost:3001/api/mammoth')

  const data = await res.json()
  const sites = await res1.json()

  return {
    props: { data, sites }
  }
} 


export default Home
