import styled from 'styled-components'
import { color } from '../public/screensizes'

import Alarms from './Alarms'

const AlarmList = styled.div`
  display: flex;
  flex-flow: column;
  padding-top: 2rem;
`

const GeneralList = styled.ul`
  list-style: none;
  margin-bottom: 2rem;
  margin-left: 3rem;
`

const AlarmListItem = styled.li`
  display: flex;
  align-items: center;
  font-size: 1.4rem;
  margin-bottom: 1rem;
`

const ListTitle = styled.h2`
  font-size: 1.8rem;
  margin-bottom: 1rem;
`

const DetailsAlarmContainer = styled.p`
  display: flex;
  flex-flow: column;
  border-bottom: 1px solid ${color.jctDarkGray};
  margin-left: 2rem;
`

const AlarmTitle = styled.h2`
  font-size: 1.8rem;
  font-weight: 500;
`

const AlarmOnOff = styled.div`
  font-size: 1.8rem;
`

const AlarmOnOffNum = styled.span`
  font-weight: bold;
  font-size: 1.8rem;
`

const MPCAlarmList = props => (
  <AlarmList>
    <GeneralList>
      <ListTitle>Water</ListTitle>
      <DetailsAlarmContainer>
        <AlarmTitle>Alarm</AlarmTitle>
      </DetailsAlarmContainer>
    </GeneralList>
  </AlarmList>
)

export default MPCAlarmList
