import styled from 'styled-components'
import fetch from 'isomorphic-unfetch'
import Link from 'next/link'
import { withOrientationChange } from 'react-device-detect'

import NewLayout from '../../components/NewLayout'
import NewMobileLayout from '../../components/NewMobileLayout'
import NewDetails from '../../components/NewDetails'
import NewMobileDetails from '../../components/NewMobileDetails'
import NewMobileDoughnutChart from '../../components/NewMobileDoughnutChart'
import DoughnutChart from '../../components/DoughnutChart'
import NewMobileCarousel from '../../components/NewMobileCarousel'
// import Layout from '../../../components/Layout'
// import MobileLayout from '../../../components/MobileLayout'
// import MobileCarousel from '../../../components/MobileCarousel'
// import FullDetails from '../../../components/FullDetails'
// import Details from '../../../components/Details'


let Home = (props, { data, isMobileView }) => {
  const { isLandscape, isPortrait } = props

  if(isLandscape){
    return(
      <NewMobileLayout company="mammoth" site_name='University' date='01/01/2020' time='5:20 PM'>
        <NewMobileDetails resourceType='Water' alarmOn={12} alarmOff={10}>
          <NewMobileDoughnutChart name={'Tank 1'} currentValue={7} totalValue={16} unitOfMeasure={'ft'} />
          <NewMobileDoughnutChart name={'Tank 2'} currentValue={9.6} totalValue={16} unitOfMeasure={'ft'} />
          <NewMobileDoughnutChart name={'Tank 3'} currentValue={9.6} totalValue={16} unitOfMeasure={'ft'} />
          <NewMobileDoughnutChart name={'Tank 4'} currentValue={9.6} totalValue={16} unitOfMeasure={'ft'} />
        </NewMobileDetails>
        <NewMobileDetails resourceType='Oil' alarmOn={12} alarmOff={10}>
          <NewMobileDoughnutChart name={'Tank 1'} currentValue={9.6} totalValue={16} unitOfMeasure={'ft'} />
        </NewMobileDetails>
      </NewMobileLayout>
    )
  }

  if(isPortrait){
    return(
      <NewMobileLayout sites={
          props.data.map((site, i) => (
            <li key={i}>
              <Link href={`jct/${site.BATTERY.toLowerCase()}`}><a>{site.BATTERY}</a></Link>
            </li>
          ))
        }company="jct" site_name='University' date='01/01/2020' time='5:20 PM'>
        <NewMobileDetails resourceType='Water' alarmOn={12} alarmOff={10}>
          <NewMobileCarousel slideNum={4}>
            <NewMobileDoughnutChart name={'Tank 1'} currentValue={7} totalValue={16} alarmOn={12} alarmOff={10} unitOfMeasure={'ft'} />
            <NewMobileDoughnutChart name={'Tank 2'} currentValue={9.6} totalValue={16} alarmOn={12} alarmOff={10} unitOfMeasure={'ft'} />
            <NewMobileDoughnutChart name={'Tank 3'} currentValue={12} totalValue={16} alarmOn={12} alarmOff={10} unitOfMeasure={'ft'} />
            <NewMobileDoughnutChart name={'Tank 4'} currentValue={13} totalValue={16} alarmOn={12} alarmOff={10} unitOfMeasure={'ft'} />
            <NewMobileDoughnutChart name={'Tank 5'} currentValue={9.6} totalValue={16} alarmOn={12} alarmOff={10} unitOfMeasure={'ft'} />
            <NewMobileDoughnutChart name={'Tank 6'} currentValue={9.6} totalValue={16} alarmOn={12} alarmOff={10} unitOfMeasure={'ft'} />
          </NewMobileCarousel>
        </NewMobileDetails>

        <NewMobileDetails resourceType='Oil' alarmOn={12} alarmOff={10}>
          <NewMobileDoughnutChart name={'Tank 1'} currentValue={9.6} totalValue={16} unitOfMeasure={'ft'} />
        </NewMobileDetails>
      </NewMobileLayout>
    )
  }

  return(
    <NewLayout
      company="jct" 
      sites={
        props.data.map((site, i) => (
          <li key={i}>
            <Link href={`jct/${site.BATTERY.toLowerCase()}`}><a>{site.BATTERY}</a></Link>
          </li>
        ))
      }
    />
  )
}

export async function getServerSideProps(ctx){
  const res = await fetch('http://localhost:3001/api/jct')
  const data = await res.json();

  let isMobileView = ctx.req.headers['user-agent'].search(/Android|iPhone/i)

    if(isMobileView < 0){
      return {
        props: {
          isMobileView: false,
          data
        }
      }
    } else {
      return {
        props: {
          isMobileView: true,
          data
        }
      }
    }
} 

Home = withOrientationChange(Home)


export default Home
