import styled from 'styled-components'
import { standard, retina, color } from '../public/screensizes'
import Link from 'next/link'
import { useCompany } from './CompanyProvider'
import fetch from 'isomorphic-unfetch'
import { useEffect } from 'react'

const SidebarContainer = styled.div`
  width: 35rem;
  background-color: ${color.jctDarkGray};
  display: grid;
  grid-template-rows: 12.5rem 1fr;
  position: sticky;
`

const CompanyContainer = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  height: 12.5rem;

  img{
    height: 75%;
  }
`

const SiteNavContainer = styled.div`
  width: 100%;
  height: 100%;
  padding-left: 3rem;
  padding-top: 1rem;

  h1{
    color: #fff;
    font-size: 18px;
    font-weight: 500;
  }
`

const SiteLinks = styled.nav`
  ul{
    list-style: none;
    margin-top: 1.5rem;
  }

  li{
    height: 5rem;
    display: flex;
    align-items: center;
    padding-left: 2rem;
    margin-bottom: 1rem;
  }

  li:hover{
    background-color: ${color.jctOrange};
  }

  a{
    color: #fff;
    text-decoration: none;
    font-size: 16px;
  }
`

const Sidebar = (props, {ctx}) => {
  console.log(ctx);
  const list = useCompany();

  useEffect(() => {
    async function fetchData() {
      // You can await here
      const response = await fetch('http://localhost:3001/api/jct');
      const json = await response.json();

      console.log(json);
    }
    fetchData();
  }, []); // Or [] if effect doesn't need props or state

  return(
    <SidebarContainer>
      <CompanyContainer>
        <img src="/static/jct-logo.png" />
      </CompanyContainer>
      <SiteNavContainer>
        <h1>Sites</h1>
        <SiteLinks>
          {props.sites}
          {/* {
            list[0].map(comp => (
              <ul>
                <li>
                  <Link href={`/jct/${comp}`}><a>{comp}</a></Link>
                </li>
              </ul>
            ))
          } */}
        </SiteLinks>
      </SiteNavContainer>
    </SidebarContainer>
  )
}

Sidebar.getInitalProps = async ctx => {
  console.log('hello');
}

export default Sidebar
