        tPumpStop={univData[0].V4}
        alarmOn={univData[0].V5}
        alarmOff={univData[0].V6}
      />
    </MobileLayout>
    :
    <Layout company_name={"Mammoth"} site_name={"University"}>
      <FullDetails 
        name={data[0].S1} 
        currentValue={data[0].V1}
        totalValue={16}
        unitOfMeasure={data[0].S2.toLowerCase()}
        tPumpOn={data[0].V3}
        tPumpOff={data[0].V4}
        alarmOn={data[0].V5}
        alarmOff={data[0].V6}
      />
    </Layout>
)

export async function getServerSideProps(ctx){
  // const univRes = await fetch('http://jct-systems.com/api/university')
  // const cehRes = await fetch('http://jct-systems.com/api/ceh')

  const res = await fetch('http://localhost:3001/api/university')

  const data = await res.json();

  let isMobileView = ctx.req.headers['user-agent'].search(/Android|iPhone/i)

  if(isMobileView < 0){
    return {
      props: { data, isMobileView: false }
    }
  } else {
    return {
      props: { data, isMobileView: true }
    }
  }
} 


export default Home
