import styled from 'styled-components'
import fetch from 'isomorphic-unfetch'

import Layout from '../../components/Layout'
import MobileLayout from '../../components/MobileLayout'
import Details from '../../components/Details'


const JCTBTY = ({ data, isMobileView }) => (
  isMobileView ?
    <MobileLayout company_name={"JCT"} site_name={"JCTBTY"}>
      <Details 
        name={cehData[0].S1} 
        currentValue={cehData[0].V1}
        totalValue={16}
        unitOfMeasure={cehData[0].S2.toLowerCase()}
        tPumpOn={cehData[0].V3}
        tPumpOff={cehData[0].V4}
        alarmOn={cehData[0].V5}
        alarmOff={cehData[0].V6}
      />
    </MobileLayout>
    :
    <Layout company_name={"JCT"} site_name={"JCTBTY"}>
      <Details 
        name={data[0].S1} 
        unitOfMeasure={data[0].S2.toLowerCase()}
        currentValue={data[0].V1}
        totalValue={16}
        tPumpOn={data[0].V3}
        tPumpOff={data[0].V4}
        alarmOn={data[0].V5}
        alarmOff={data[0].V6}
      />

      <Details 
        name={data[0].S3} 
        unitOfMeasure={data[0].S2.toLowerCase()}
        currentValue={data[0].V1}
        totalValue={16}
        tPumpOn={data[0].V3}
        tPumpOff={data[0].V4}
        alarmOn={data[0].V5}
        alarmOff={data[0].V6}
      />
    </Layout>
)

export async function getServerSideProps(ctx){
  // const univRes = await fetch('http://jct-systems.com/api/university')
  // const cehRes = await fetch('http://jct-systems.com/api/ceh')

  const res = await fetch('http://localhost:3001/api/jctbty')
  const data = await res.json();

  let isMobileView = ctx.req.headers['user-agent'].search(/Android|iPhone/i)

  if(isMobileView < 0){
    return {
      props: { data, isMobileView: false }
    }
  } else {
    return {
      props: { data, isMobileView: true }
    }
  }
} 


export default JCTBTY
