import fetch from 'isomorphic-unfetch'
import Link from 'next/link'
import { isBrowser, isMobile } from 'react-device-detect'

import NewLayout from '../../../components/NewLayout'
import NewDetails from '../../../components/NewDetails'
import DoughnutChart from '../../../components/DoughnutChart'


const Home = ({ data, sites }) => {
  return(
    <NewLayout
      company="jct" 
      sites={
        data.map((site, i) => (
          <li key={i}>
            <Link href={`jct/${site.BATTERY.toLowerCase()}`}><a>{site.BATTERY}</a></Link>
          </li>
        ))
      }
    />
  )
}

export async function getServerSideProps(ctx){
  const res = await fetch('http://jct-systems.com/api/ceh')
  const res1 = await fetch('http://localhost:3001/api/mammoth')

  const data = await res.json()
  const sites = await res1.json()

  return {
    props: { data, sites }
  }
} 


export default Home
